        //     expanded
        //   });          
    }

    foo() {

        const { prevScrollpos } = this.state;

        const currentScrollPos = window.pageYOffset;
        const expanded = prevScrollpos > currentScrollPos;

        this.setState({
            prevScrollpos: currentScrollPos,
            expanded
          }); 
    }



