  state = {
    persons: [
      {name: 'Bob', age: 28 },
      {name: 'Sue', age: 29 },
      {name: 'Joe', age: 26 }
    ],
    sideMenuOpen: false
  }

  sideMenuToggleClickHandler = () => {
      this.setState((prevState) => {
        return {sideMenuOpen: !prevState.sideMenuOpen}
      });
  }

  chromeClickHandler = () => {
    this.setState({sideMenuOpen: false});
  }

  switchNameHandler = (newName) => {
    this.setState({ 
      persons: [
        {name: [newName], age: 20 },
        {name: 'Suzy', age: 20 },
        {name: 'Joe', age: 20 }
      ]
    })
  }

   changedNameHandler = (event) => {
    this.setState({ 
      persons: [
        {name: 'Max', age: 20 },
        {name: event.target.value, age: 20 },
        {name: 'Joe', age: 20 }
      ]
    })
  }

  render() {

    let sideMenu;
    let chrome;

